import json
import os
import base64
from datetime import datetime, timedelta

class DashboardRenderer:
    def __init__(self, stats_data):
        self.stats_data = stats_data
        # Load local logo for embedding
        self.logo_base64 = ""
        try:
            logo_path = os.path.join(os.path.dirname(__file__), "..", "..", "commitpulse-cloud", "public", "logo.png")
            if os.path.exists(logo_path):
                with open(logo_path, "rb") as f:
                    self.logo_base64 = base64.b64encode(f.read()).decode()
        except:
            pass

    def render(self, output_path="stats_dashboard.html"):
        logo_src = f"data:image/png;base64,{self.logo_base64}" if self.logo_base64 else "https://raw.githubusercontent.com/samolubukun/commitpulse/main/favicon.png"
        
        html_template = """
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Commit Pulse | Local Dashboard</title>
    <link href="https://fonts.googleapis.com/css2?family=Outfit:wght@300;400;500;600;700;800;900&family=Inter:wght@400;500;600;700&display=swap" rel="stylesheet">
    <style>
        :root {
            --bg-dark: #0d1117;
            --card-bg: rgba(22, 27, 34, 0.7);
            --border: rgba(255, 255, 255, 0.1);
            --text-main: #f0f6fc;
            --text-dim: #8b949e;
            --accent-emerald: #238636;
            --accent-green: #39d353;
            --accent-blue: #58a6ff;
            --accent-red: #f85149;
            --accent-yellow: #f2cc60;
            --glass-blur: blur(20px);
            
            /* Heatmap Colors */
            --h-0: #161b22;
            --h-1: #0e4429;
            --h-2: #006d32;
            --h-3: #26a641;
            --h-4: #39d353;
        }

        * {
            box-sizing: border-box;
            transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }

        body {
            font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
            background-color: var(--bg-dark);
            background-image: 
                radial-gradient(circle at 0% 0%, rgba(35, 134, 54, 0.05) 0%, transparent 50%),
                radial-gradient(circle at 100% 100%, rgba(88, 166, 255, 0.03) 0%, transparent 50%);
            color: var(--text-main);
            margin: 0;
            padding: 0;
            overflow-x: hidden;
            min-height: 100vh;
        }

        header {
            padding: 24px 60px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            position: sticky;
            top: 0;
            z-index: 100;
            background: rgba(13, 17, 23, 0.8);
            backdrop-filter: var(--glass-blur);
            border-bottom: 1px solid var(--border);
        }

        .header-left {
            display: flex;
            align-items: center;
            gap: 16px;
        }

        .logo-img {
            width: 40px;
            height: 40px;
            border-radius: 12px;
            box-shadow: 0 0 20px rgba(35, 134, 54, 0.2);
        }

        h1 {
            font-family: 'Outfit', sans-serif;
            font-size: 24px;
            font-weight: 800;
            margin: 0;
            background: linear-gradient(90deg, #fff, #8b949e);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            font-style: italic;
        }

        .container {
            max-width: 1400px;
            margin: 0 auto;
            padding: 60px;
        }

        .repo-card {
            background: rgba(22, 27, 34, 0.5);
            border: 1px solid var(--border);
            border-radius: 24px;
            padding: 40px;
            backdrop-filter: var(--glass-blur);
            position: relative;
            overflow: hidden;
            margin-bottom: 40px;
        }

        .repo-card::before {
            content: '';
            position: absolute;
            left: 0; top: 0; width: 4px; height: 100%;
            background: var(--accent-emerald);
            opacity: 0;
        }

        .repo-card:hover {
            border-color: var(--accent-blue);
            transform: translateY(-2px);
            background: rgba(22, 27, 34, 0.7);
        }

        .repo-card:hover::before {
            opacity: 1;
        }

        .repo-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 24px;
        }

        .repo-name-group {
            display: flex;
            align-items: center;
            gap: 16px;
        }

        .repo-name {
            font-family: 'Outfit', sans-serif;
            font-size: 32px;
            font-weight: 800;
            letter-spacing: -1px;
        }

        .commits-badge {
            background: var(--accent-emerald);
            color: white;
            padding: 8px 16px;
            border-radius: 10px;
            font-weight: 800;
            font-size: 14px;
            box-shadow: 0 0 20px rgba(35, 134, 54, 0.2);
        }

        .est-time-badge {
            display: inline-flex;
            align-items: center;
            gap: 8px;
            background: rgba(255, 255, 255, 0.05);
            border: 1px solid var(--border);
            padding: 6px 12px;
            border-radius: 8px;
            font-size: 11px;
            font-weight: 900;
            text-transform: uppercase;
            letter-spacing: 1px;
            color: var(--text-dim);
            margin-bottom: 24px;
        }

        .lang-pills {
            display: flex;
            gap: 8px;
            margin-bottom: 32px;
        }

        .lang-pill {
            font-size: 10px;
            font-weight: 900;
            text-transform: uppercase;
            letter-spacing: 1px;
            color: var(--text-dim);
            padding: 4px 10px;
            border-radius: 6px;
            border: 1px solid rgba(255, 255, 255, 0.05);
            background: rgba(255, 255, 255, 0.02);
        }

        .meta-row {
            display: flex;
            flex-wrap: wrap;
            gap: 12px;
            margin-bottom: 40px;
        }

        .meta-pill {
            background: rgba(255, 255, 255, 0.05);
            border: 1px solid var(--border);
            padding: 6px 16px;
            border-radius: 100px;
            font-size: 13px;
            font-weight: 600;
            color: var(--text-dim);
            display: flex;
            align-items: center;
            gap: 8px;
        }

        .meta-pill.accent {
            background: rgba(35, 134, 54, 0.1);
            border-color: var(--accent-emerald);
            color: var(--accent-emerald);
        }

        .meta-pill.plus { color: var(--accent-green); background: rgba(57, 211, 83, 0.05); border-color: rgba(57, 211, 83, 0.2); }
        .meta-pill.minus { color: var(--accent-red); background: rgba(248, 81, 73, 0.05); border-color: rgba(248, 81, 73, 0.2); }

        .section {
            background: rgba(0, 0, 0, 0.2);
            border-radius: 20px;
            border: 1px solid var(--border);
            padding: 32px;
            margin-bottom: 32px;
        }

        .section-title {
            font-size: 11px;
            font-weight: 800;
            text-transform: uppercase;
            letter-spacing: 2px;
            color: var(--text-dim);
            margin-bottom: 24px;
            display: flex;
            justify-content: space-between;
            align-items: center;
        }

        .grid-2 {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 32px;
        }

        /* Heatmap Scrollable Wrapper */
        .heatmap-scroll {
            overflow-x: auto;
            display: flex;
            gap: 20px;
            padding-bottom: 12px;
        }

        .heatmap-month-column {
            display: flex;
            flex-direction: column;
            gap: 12px;
        }

        .heatmap-cells {
            display: flex;
            flex-direction: column;
            flex-wrap: wrap;
            height: 124px;
            gap: 4px;
        }

        .heatmap-cell {
            width: 14px;
            height: 14px;
            border-radius: 2px;
            background: var(--h-0);
        }

        /* Activity Pulse Component */
        .pulse-bar {
            height: 12px;
            border-radius: 100px;
            background: rgba(255, 255, 255, 0.05);
            display: flex;
            overflow: hidden;
            margin-bottom: 32px;
        }

        .pulse-segments {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 20px;
        }

        .pulse-item {
            display: flex;
            align-items: center;
            gap: 12px;
        }

        .pulse-icon-box {
            padding: 8px;
            border-radius: 10px;
            background: rgba(255, 255, 255, 0.05);
            border: 1px solid var(--border);
        }

        .pulse-details .label {
            font-size: 10px;
            font-weight: 900;
            text-transform: uppercase;
            letter-spacing: 1px;
            color: var(--text-dim);
        }

        .pulse-details .value {
            font-size: 15px;
            font-weight: 700;
            color: white;
            margin-top: 2px;
        }

        /* Contributors */
        .contributor-list {
            display: flex;
            flex-wrap: wrap;
            gap: 16px;
        }

        .contributor-pill {
            background: rgba(255, 255, 255, 0.03);
            border: 1px solid var(--border);
            padding: 8px 16px;
            border-radius: 12px;
            display: flex;
            align-items: center;
            gap: 12px;
            cursor: pointer;
        }

        .contributor-pill:hover {
            background: rgba(255, 255, 255, 0.08);
            border-color: var(--accent-blue);
        }

        .contributor-avatar {
            width: 32px;
            height: 32px;
            border-radius: 50%;
            border: 2px solid var(--border);
        }

        .contributor-info .name {
            font-size: 14px;
            font-weight: 700;
        }

        .contributor-info .commits {
            font-size: 10px;
            color: var(--text-dim);
        }

        footer {
            max-width: 1400px;
            margin: 0 auto;
            padding: 60px;
            border-top: 1px solid var(--border);
            display: flex;
            justify-content: space-between;
            align-items: center;
            opacity: 0.6;
            filter: grayscale(1);
        }

        footer:hover { opacity: 1; filter: none; }

        .footer-logo { display:flex; align-items:center; gap:10px; font-weight:700; font-style:italic; }

        @keyframes slideUp {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
        }

        .repo-card { animation: slideUp 0.8s cubic-bezier(0.2, 0.8, 0.2, 1) forwards; }
    </style>
</head>
<body>
    <header>
        <div class="header-left">
            <img src="__LOGO_SRC__" class="logo-img" alt="Logo" onerror="this.src='https://www.gravatar.com/avatar/0?d=identicon'">
            <h1>Commit Pulse</h1>
        </div>
        <div class="meta-pill accent">
            Local Engineering Insight
        </div>
    </header>

    <div class="container" id="container"></div>

    <footer>
        <div class="footer-logo">
            <img src="__LOGO_SRC__" width="24" height="24" style="border-radius:6px">
            Commit Pulse
        </div>
        <div style="font-size: 13px; font-weight: 500; color: var(--text-dim)">
            &copy; 2026 Samuel Olubukun. Crafted for top-tier engineers.
        </div>
        <div style="font-size: 13px; font-weight: 700; display:flex; gap:20px">
            <a href="https://github.com/samolubukun/commitpulse" style="color:inherit;text-decoration:none">GitHub</a>
            <a href="https://pypi.org/project/commitpulse/" style="color:inherit;text-decoration:none">PyPI</a>
        </div>
    </footer>

    <script>
        const stats = __STATS_DATA__;
        
        function createHeatmap(repo) {
            const start = new Date(repo.first_commit);
            const end = new Date(repo.last_commit);
            const current = new Date(start.getFullYear(), start.getMonth(), 1);
            const lastDay = new Date(end.getFullYear(), end.getMonth() + 1, 0);
            
            const months = {};
            while (current <= lastDay) {
                const monthKey = `${current.getFullYear()}-${String(current.getMonth() + 1).padStart(2, '0')}`;
                if (!months[monthKey]) {
                    months[monthKey] = { name: current.toLocaleString('default', { month: 'short' }), year: current.getFullYear(), days: [] };
                }
                const dateKey = current.toISOString().split('T')[0];
                months[monthKey].days.push({ date: dateKey, count: repo.heatmap[dateKey] || 0 });
                current.setDate(current.getDate() + 1);
            }

            return Object.values(months).map(m => `
                <div class="heatmap-month-column">
                    <div style="font-size: 10px; font-weight: 800; color: var(--accent-blue); text-transform: uppercase;">${m.name} ${m.year}</div>
                    <div class="heatmap-cells">
                        ${m.days.map(d => {
                            let intensity = "";
                            if (d.count > 0) {
                                if (d.count < 3) intensity = "var(--h-1)";
                                else if (d.count < 6) intensity = "var(--h-2)";
                                else if (d.count < 10) intensity = "var(--h-3)";
                                else intensity = "var(--h-4)";
                            }
                            return `<div class="heatmap-cell" style="background: ${intensity || 'var(--h-0)'}" title="${d.count} commits on ${d.date}"></div>`;
                        }).join('')}
                    </div>
                </div>
            `).join('');
        }

        function createPulse(segments) {
            const keys = [
                { label: 'Early Bird', key: 'Early Bird', color: '#58a6ff', icon: '' },
                { label: 'Morning', key: 'Morning', color: '#39d353', icon: '' },
                { label: 'Afternoon', key: 'Afternoon', color: '#f2cc60', icon: '' },
                { label: 'Late Night', key: 'Late Night', color: '#f85149', icon: '' },
            ];
            const total = Object.values(segments).reduce((a, b) => a + b, 0) || 1;

            return {
                bar: keys.map(k => {
                    const w = ((segments[k.key] || 0) / total) * 100;
                    return w > 0 ? `<div style="width: ${w}%; background: ${k.color}"></div>` : '';
                }).join(''),
                segments: keys.map(k => {
                    const c = segments[k.key] || 0;
                    const p = Math.round((c / total) * 100);
                    return `
                        <div class="pulse-item">
                            <div class="pulse-icon-box" style="color: ${k.color}">${k.icon}</div>
                            <div class="pulse-details">
                                <div class="label">${k.label}</div>
                                <div class="value">${p}% <span style="opacity: 0.3; font-weight: 400; font-size: 11px">(${c})</span></div>
                            </div>
                        </div>
                    `;
                }).join('')
            };
        }

        const container = document.getElementById('container');
        stats.forEach((repo, idx) => {
            const pulse = createPulse(repo.activity_pulse || {});
            const card = document.createElement('div');
            card.className = 'repo-card';
            card.style.animationDelay = `${idx * 0.1}s`;
            
            card.innerHTML = `
                <div class="repo-header">
                    <div class="repo-name-group">
                        <img src="__LOGO_SRC__" width="32" height="32" style="border-radius:8px">
                        <div class="repo-name">${repo.name}</div>
                    </div>
                    <div class="commits-badge">${repo.total_commits} Total Commits</div>
                </div>

                ${repo.estimated_hours ? `
                    <div class="est-time-badge">
                        <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="3" stroke-linecap="round" stroke-linejoin="round"><path d="M13 2L3 14h9l-1 8 10-12h-9l1-8z"/></svg>
                        Est. Engineering Time: ${repo.estimated_hours} Hours
                    </div>
                ` : ''}

                <div class="lang-pills">
                    ${(repo.top_languages || []).map(l => `<span class="lang-pill">${l}</span>`).join('')}
                </div>

                <div class="meta-row">
                    <div class="meta-pill">
                        <span style="opacity: 0.5; font-size: 10px; font-weight: 900; text-transform: uppercase;">Timeline</span>
                        ${repo.first_commit} to ${repo.last_commit}
                    </div>
                    <div class="meta-pill accent">
                        <span style="opacity: 0.5; font-size: 10px; font-weight: 900; text-transform: uppercase;">Peak</span>
                        ${repo.peak_hour}
                    </div>
                    ${repo.lines_added ? `
                        <div class="meta-pill plus">+ ${repo.lines_added.toLocaleString()}</div>
                        <div class="meta-pill minus">- ${repo.lines_deleted.toLocaleString()}</div>
                    ` : ''}
                </div>

                <div class="section">
                    <div class="section-title">
                        <span>Commit Contributions (Full History)</span>
                        <div style="display: flex; gap: 4px; align-items: center;">
                            <span style="font-size: 9px; opacity: 0.5;">Less</span>
                            <div class="heatmap-cell" style="width:10px; height:10px; background:var(--h-0)"></div>
                            <div class="heatmap-cell" style="width:10px; height:10px; background:var(--h-1)"></div>
                            <div class="heatmap-cell" style="width:10px; height:10px; background:var(--h-2)"></div>
                            <div class="heatmap-cell" style="width:10px; height:10px; background:var(--h-3)"></div>
                            <div class="heatmap-cell" style="width:10px; height:10px; background:var(--h-4)"></div>
                            <span style="font-size: 9px; opacity: 0.5;">More</span>
                        </div>
                    </div>
                    <div class="heatmap-scroll">
                        ${createHeatmap(repo)}
                    </div>
                </div>

                <div class="grid-2">
                    <div class="section">
                        <div class="section-title">Activity Pulse</div>
                        <div class="pulse-bar">${pulse.bar}</div>
                        <div class="pulse-segments">${pulse.segments}</div>
                    </div>
                    <div class="section">
                        <div class="section-title">Top Engineering Impact</div>
                        <div class="contributor-list">
                            ${repo.contributors.map(c => `
                                <div class="contributor-pill" title="${c.email}">
                                    <img src="${c.avatar}" class="contributor-avatar" onerror="this.src='https://www.gravatar.com/avatar/0?d=identicon'">
                                    <div class="contributor-info">
                                        <div class="name">${c.name}</div>
                                        <div class="commits">${c.commits} commits</div>
                                    </div>
                                </div>
                            `).join('')}
                        </div>
                    </div>
                </div>
            `;
            container.appendChild(card);
        });
    </script>
</body>
</html>
""".replace("__STATS_DATA__", json.dumps(self.stats_data)).replace("__LOGO_SRC__", logo_src)
        
        with open(output_path, "w", encoding="utf-8") as f:
            f.write(html_template)
        
        return os.path.abspath(output_path)
